// Extract the agent output
const agentOutput = $input.first().json.output;

// Remove any code block markers
let cleanOutput = agentOutput
  .replace(/```json\s*/g, '')
  .replace(/```html\s*/g, '')
  .replace(/```/g, '')
  .trim();

// Preprocess: Convert literal \n to actual newlines for proper JSON parsing
function preprocessJSON(str) {
  if (typeof str !== 'string') return str;

  // Replace literal \n with actual newline character
  // This handles the case where \n is in the string as text, not as escape sequence
  return str.replace(/\\n/g, '\n').replace(/\\"/g, '"');
}

// Function to aggressively parse JSON that might be double-stringified
function aggressiveParse(input, maxDepth = 5) {
  let current = input;

  for (let i = 0; i < maxDepth; i++) {
    // If it's already an object with 'text' field, we're done
    if (typeof current === 'object' && current !== null && current.text) {
      return current;
    }

    // If it's not a string, we can't parse further
    if (typeof current !== 'string') {
      break;
    }

    // Preprocess the string before parsing
    const preprocessed = preprocessJSON(current);

    // Try to parse
    try {
      current = JSON.parse(preprocessed);
    } catch (e) {
      // If parse fails, try without preprocessing
      try {
        current = JSON.parse(current);
      } catch (e2) {
        // If both fail, we're done
        break;
      }
    }
  }

  return current;
}

// First parse: Get the initial structure
let parsed = aggressiveParse(cleanOutput);

// If we have an object, check all possible locations for the data
if (parsed && typeof parsed === 'object') {
  // Case 1: Direct format {text, question, quick_replies}
  if (parsed.text) {
    return [{
      json: {
        message: parsed.text,
        question: parsed.question || '',
        quick_replies: parsed.quick_replies || []
      }
    }];
  }

  // Case 2: Nested in 'message' field
  if (parsed.message) {
    const nested = aggressiveParse(parsed.message);

    if (nested && typeof nested === 'object' && nested.text) {
      return [{
        json: {
          message: nested.text,
          question: nested.question || '',
          quick_replies: nested.quick_replies || []
        }
      }];
    }

    // If message is just a string (not nested JSON), use it
    if (typeof nested === 'string') {
      return [{
        json: {
          message: nested,
          question: parsed.question || '',
          quick_replies: parsed.quick_replies || []
        }
      }];
    }
  }

  // Case 3: Use whatever we have
  return [{
    json: {
      message: parsed.message || JSON.stringify(parsed),
      question: parsed.question || '',
      quick_replies: parsed.quick_replies || []
    }
  }];
}

// If all parsing fails, return as plain text
return [{
  json: {
    message: cleanOutput,
    question: '',
    quick_replies: []
  }
}];

